,p_display_name=>'AS Passkey'
,p_category=>'MISC'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('DYNAMIC ACTION','AS_PASSKEY'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'procedure load_ddl( p_plugin_name varchar2, p_file_name varchar2 )',
'is',
'  l_ddl blob;',
'  l_src clob;',
'  src_offset number := 1;',
'  dest_offset number := 1;',
'  lang_context number := sys.dbms_lob.default_lang_ctx;',
'  warning number;',
'begin',
'  select pgf.file_content',
'  into l_ddl',
'  from apex_appl_plugin_files pgf',
'  where pgf.plugin_name = p_plugin_name',
'  and   pgf.application_id = apex_application.g_flow_id',
'  and   pgf.file_name = p_file_name;',
'  --',
'  if l_ddl is not null and sys.dbms_lob.getlength( l_ddl ) > 0',
'  then',
'    sys.dbms_lob.createtemporary( l_src, true, sys.dbms_lob.call );',
'    sys.dbms_lob.converttoclob( l_src, l_ddl, sys.dbms_lob.lobmaxsize, dest_offset, src_offset, nls_charset_id( ''AL32UTF8'' ), lang_context, warning );',
'    l_src := rtrim( l_src, '' /'' || chr(10) || chr(13) );',
'    execute immediate l_src;',
'    sys.dbms_lob.freetemporary( l_src );',
'  end if;',
'end load_ddl;',
'',
'function init_table',
'return boolean',
'is',
'  e_not_declared exception;',
'  pragma exception_init( e_not_declared, -6550 );',
'begin',
'  begin',
'    execute immediate ''declare x as_user_passkeys%rowtype; begin null; end;'';',
'  exception',
'    when e_not_declared then',
'      apex_debug.warn( ''table as_user_passkeys does not exist'' );',
'      execute immediate ''',
'create table as_user_passkeys',
'  ( id number generated always as identity constraint as_user_passkeys2_pk primary key',
'  , workspace_id number not null',
'  , app_id       number not null',
'  , name         varchar2(4000 char) not null',
'  , extra        varchar2(4000 char)',
'  , passkeys     clob',
'  )',
''';',
'      apex_debug.trace( ''table as_user_passkeys created'' );',
'      execute immediate ''',
'alter table as_user_passkeys',
'  add constraint as_user_passkeys_uk unique( workspace_id, app_id, name )',
''';',
'      apex_debug.trace( ''unique key for as_user_passkeys created'' );',
'  end;',
'  execute immediate ''declare x as_user_passkeys%rowtype; begin null; end;'';',
'  apex_debug.info( ''table as_user_passkeys exists'' );',
'  return true;',
'end init_table;',
'',
'function init_package( p_plugin apex_plugin.t_plugin )',
'return boolean',
'is',
'  l_version varchar2(100);',
'  cursor c_aap( cp_app_id number )',
'  is',
'    select aap.version_identifier',
'    from apex_appl_plugins aap',
'    where aap.application_id = cp_app_id',
'    and   aap.name = p_plugin.name;',
'  r_aap c_aap%rowtype;',
'  e_not_declared exception;',
'  pragma exception_init( e_not_declared, -6550 );',
'begin',
'  begin',
'    execute immediate ''declare x varchar2(9); begin x := as_passkeys.get_version; end;'';',
'  exception',
'    when e_not_declared then',
'      apex_debug.warn( ''package as_passkeys does not exist'' );',
'      --',
'      load_ddl( p_plugin.name, ''as_passkeys.pks'' );',
'      load_ddl( p_plugin.name, ''as_passkeys.pkb'' );',
'  end;',
'  execute immediate ''begin :x := as_passkeys.get_version; end;'' using out l_version;',
'  apex_debug.info( ''package as_passkeys exists, version %s'', l_version );',
'  --',
'  open c_aap( v( ''APP_ID'' ) );',
'  fetch c_aap into r_aap;',
'  close c_aap;',
'  --',
'  if l_version < r_aap.version_identifier',
'  then',
'    apex_debug.warn( ''package version %s, load new version %s'', l_version, r_aap.version_identifier );',
'    load_ddl( p_plugin.name, ''as_passkeys.pkb'' );',
'    apex_debug.info( ''new version loaded'' );',
'  end if;',
'  return true;',
'end init_package;',
'',
'function init_plugin_and_render',
'  ( p_dynamic_action apex_plugin.t_dynamic_action',
'  , p_plugin         apex_plugin.t_plugin',
'  )',
'return apex_plugin.t_dynamic_action_render_result',
'is',
'  l_rv apex_plugin.t_dynamic_action_render_result;',
'begin',
'  if init_table and init_package( p_plugin )',
'  then',
'    execute immediate ''begin :x := as_passkeys.render( :p1, :p2 ); end;'' using out l_rv, p_dynamic_action, p_plugin;',
'  end if;',
'  return l_rv;',
'end init_plugin_and_render;'))
,p_api_version=>2
,p_render_function=>'init_plugin_and_render'
,p_ajax_function=>'as_passkeys.ajax'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'0.800'
,p_about_url=>'https://github.com/antonscheffer'
,p_files_version=>6
);
